import React, { useRef, useState } from 'react';
import { ComposableMap, Geographies, Geography, ZoomableGroup } from 'react-simple-maps';
import axios from 'a'

// Link do pliku GeoJSON z województwami Polski
const geoUrl = "https://raw.githubusercontent.com/ppatrzyk/polska-geojson/master/wojewodztwa/wojewodztwa-medium.geojson";

const MapaPolski = () => {
    const [hoveredVoivodeship, setHoveredVoivodeship] = useState(null);
    const geographyRefs = useRef({});

    return (
      <div style={{ width: "100%", height: "100vh", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <h1>Mapa Polski</h1>
        <ComposableMap
          projection="geoMercator"
          projectionConfig={{
            scale: 1500, // Zwiększona skala dla większej mapy
            center: [19, 52], // Centrum mapy na Polskę
          }}
          style={{
            width: "80%", // Szerokość mapy
            height: "auto",
          }}
        >
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => {
                const isHovered = hoveredVoivodeship === geo.rsmKey;

                return (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    onMouseEnter={() => {
                      setHoveredVoivodeship(geo.rsmKey);
                    }}
                    onMouseLeave={() => {
                      setHoveredVoivodeship(null);
                    }}
                    style={{
                      default: {
                        fill: "#EEE",
                        stroke: "#000",
                        strokeWidth: "0.5px",
                        outline: "none",
                        opacity: isHovered ? 1 : 0.5, // Przyciemnienie reszty województw
                        transition: "all 0.3s ease", // Animacja
                      },
                      hover: {
                        fill: "#EEE",
                        stroke: "#800080", // Fioletowy kolor obrysu
                        strokeWidth: "2px",
                        outline: "none",
                        filter: "drop-shadow(0 0 8px rgba(128, 0, 128, 0.8))", // Efekt glow
                        transform: "scale(1.05) translateY(-5px)", // Podniesienie i powiększenie
                        opacity: 1, // Pełna widoczność
                        transition: "all 0.3s ease", // Animacja
                      },
                      pressed: {
                        fill: "#EEE",
                        stroke: "#000",
                        strokeWidth: "2px",
                        outline: "none",
                      },
                    }}
                  />
                );
              })
            }
          </Geographies>
        </ComposableMap>
      </div>
    );
  };

  export default MapaPolski;
