import React, { useRef } from 'react';
import { ComposableMap, Geographies, Geography, ZoomableGroup } from 'react-simple-maps';

// Link do pliku GeoJSON z województwami Polski
const geoUrl = "https://raw.githubusercontent.com/ppatrzyk/polska-geojson/master/wojewodztwa/wojewodztwa-medium.geojson";

const MapaPolski = () => {
    // Referencja do elementu Geography
    const geographyRefs = useRef({});

    return (
      <div>
        <h1>Mapa Polski</h1>
        <ComposableMap
          projection="geoMercator"
          projectionConfig={{
            scale: 1500,
            center: [19, 52], // Centrum mapy na Polskę
          }}
          style={{
            width: "80%",
            height: "auto",
          }}
        >
            <Geographies geography={geoUrl}>
                {({ gerographies }) =>
                geographies.map((geo) => {
                    const ref = (el) => (geographyRefs.current[geo.rsmKey] = el)
                })} 
            </Geographies>
          {/* ZoomableGroup z domyślnym centrum i zoomem */}
          <ZoomableGroup center={[19, 52]} zoom={1}>
            <Geographies geography={geoUrl}>
              {({ geographies }) =>
                geographies.map((geo) => {
                  // Ustawienie referencji dla każdego Geography
                  const ref = (el) => (geographyRefs.current[geo.rsmKey] = el);

                  return (
                    <Geography
                      key={geo.rsmKey}
                      geography={geo}
                      ref={ref}
                      onMouseEnter={() => {
                        // Zmiana stylu po najechaniu
                        if (geographyRefs.current[geo.rsmKey]) {
                          geographyRefs.current[geo.rsmKey].style.strokeWidth = "2px";
                          geographyRefs.current[geo.rsmKey].style.fill = "#CCC";
                        }
                      }}
                      onMouseLeave={() => {
                        // Przywrócenie domyślnego stylu
                        if (geographyRefs.current[geo.rsmKey]) {
                          geographyRefs.current[geo.rsmKey].style.strokeWidth = "0.5px";
                          geographyRefs.current[geo.rsmKey].style.fill = "#EEE";
                        }
                      }}
                      onClick={() => {
                        // Obsługa kliknięcia na województwo
                        console.log("Kliknięto województwo:", geo.properties);
                      }}
                      style={{
                        default: {
                          fill: "#EEE", // Kolor wypełnienia
                          stroke: "#000", // Kolor obrysu
                          strokeWidth: "0.5px", // Grubość obrysu
                          outline: "none", // Usunięcie domyślnego outline
                        },
                        hover: {
                          fill: "#CCC", // Kolor wypełnienia po najechaniu
                          stroke: "#000", // Kolor obrysu po najechaniu
                          strokeWidth: "2px", // Grubość obrysu po najechaniu
                          outline: "none",
                        },
                        pressed: {
                          fill: "#AAA", // Kolor wypełnienia po kliknięciu
                          stroke: "#000", // Kolor obrysu po kliknięciu
                          strokeWidth: "2px", // Grubość obrysu po kliknięciu
                          outline: "none",
                        },
                      }}
                    />
                  );
                })
              }
            </Geographies>
          </ZoomableGroup>
        </ComposableMap>
      </div>
    );
  };

  export default MapaPolski;
