import React, { useRef, useState } from 'react';
import { ComposableMap, Geographies, Geography } from 'react-simple-maps';
import axios from 'axios';

const geoUrl = "https://raw.githubusercontent.com/ppatrzyk/polska-geojson/master/wojewodztwa/wojewodztwa-medium.geojson";

const MapaPolski = () => {
  const [hoveredVoivodeship, setHoveredVoivodeship] = useState(null);
  const [cities, setCities] = useState([]);
  const geographyRefs = useRef({});

  const fetchCities = async (voivodeshipName) => {
    try {
      const response = await axios.get(
        `http://api.geonames.org/searchJSON?q=${voivodeshipName}&country=PL&maxRows=10&username=Kosma`
      );

      setCities(response.data.geonames);
    } catch (error) {
      console.error("Wystąpił błąd podczas pobierania danych:", error);
    }
  };

  return (
    <div style={{ width: "100%", height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column" }}>
      <h1>Mapa Polski</h1>
      <ComposableMap
        projection="geoMercator"
        projectionConfig={{
          scale: 1500,
          center: [19, 52],
        }}
        style={{
          width: "80%",
          height: "auto",
        }}
      >
        <Geographies geography={geoUrl}>
          {({ geographies }) =>
            geographies.map((geo) => {
              const isHovered = hoveredVoivodeship === geo.rsmKey;

              return (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  onMouseEnter={() => {
                    setHoveredVoivodeship(geo.rsmKey);
                  }}
                  onMouseLeave={() => {
                    setHoveredVoivodeship(null);
                  }}
                  onClick={() => {
                    const voivodeshipName = geo.properties.nazwa;
                    console.log(voivodeshipName);
                    fetchCities(voivodeshipName);
                  }}
                  style={{
                    default: {
                      fill: "#EEE",
                      stroke: "#000",
                      strokeWidth: "0.5px",
                      outline: "none",
                      opacity: isHovered ? 1 : 0.5,
                      transition: "all 0.3s ease",
                    },
                    hover: {
                      fill: "#EEE",
                      stroke: "#800080",
                      strokeWidth: "2px",
                      outline: "none",
                      filter: "drop-shadow(0 0 8px rgba(128, 0, 128, 0.8))", 
                      transform: "scale(1.05) translateY(-5px)",
                      opacity: 1,
                      transition: "all 0.3s ease", 
                    },
                    pressed: {
                      fill: "#EEE",
                      stroke: "#000",
                      strokeWidth: "2px",
                      outline: "none",
                    },
                  }}
                />
              );
            })
          }
        </Geographies>
      </ComposableMap>

      {/* Wyświetlanie listy miast */}
      {cities.length > 0 && (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <h2>Miasta w wybranym województwie:</h2>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {cities.map((city) => (
              <li key={city.geonameId}>{city.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MapaPolski;
